class animation:
    def __init__(self,x,y,imglist):
        self.pos=pygame.Vector2(x,y)
        self.frames=[]
        for img in imglist:
            self.frames.append(pygame.image.load(img))
        self.counter=0
        self.frame_num=len(imglist)
    def next(self):
        self.counter=(self.counter+1)%self.frame_num
    def clear(self):
        for i in range(self.frame_num):
            self.frames[i]=None

class background(animation):
    def __init__(self,imglist):
        self.frames=[]
        for img in imglist:
            self.frames.append(pygame.image.load(img))
        self.counter=0
        self.frame_num=len(imglist)

class button(pygame.sprite.Sprite):
    def __init__(self,pos_x,pos_y,img1,img2):
        pygame.sprite.Sprite.__init__(self)
        self.skin = [pygame.image.load(img1), pygame.image.load(img2)]
        self.image=self.skin[0]
        self.width,self.height=self.image.get_size()
        self.pos = (pos_x, pos_y)
        self.rect = pygame.Rect(self.pos,(self.width,self.height))
        self.pos_x,self.pos_y=self.pos

buttons=pygame.sprite.Group()
bg1=background([f'background1/bg{i}.png' for i in range(98)])

stage_text=animation(300,80, [f'menu_image/stage_text_image/text{i}.png' for i in range(20)])

stages=[]
for i in range(3):
    stages.append(button(s_width/2-b_width/2, s_height/2-b_height/2, f'menu_image/pic{i+1}1.png', f'menu_image/pic{i+1}2.png'))
left_arrow=button(100,s_height/2-25,'menu_image/left_arrow.png','menu_image/left_arrow2.png')
right_arrow=button(s_width-150,s_height/2-25,'menu_image/right_arrow.png','menu_image/right_arrow2.png')
exit_bar=button(800,550,'menu_image/exit.gif','menu_image/exit2.gif')

stages[0].add(buttons)
left_arrow.add(buttons)
right_arrow.add(buttons)
exit_bar.add(buttons)



def redrawStartMenu():
    screen.blit(bg1.frames[bg1.counter], (0,0))
    screen.blit(stage_text.frames[stage_text.counter], stage_text.pos)
    buttons.draw(screen)
    pygame.display.update()


stage_num=3
def startmenu():
    count=0
    delay1=0
    menurun=True
    while menurun:

        delay1=(delay1+1)%3
        if delay1==0:
            bg1.next()
            stage_text.next()

        for event in pygame.event.get() : 
            if event.type == pygame.QUIT : 
                pygame.quit()
            mouse_pos=pygame.mouse.get_pos()
            for spr in buttons.sprites():
                if spr.rect.collidepoint(mouse_pos):
                    spr.image=spr.skin[1]
                else:
                    spr.image=spr.skin[0]
            if exit_bar.rect.collidepoint(mouse_pos) and event.type == MOUSEBUTTONDOWN:
                pygame.quit()
            if left_arrow.rect.collidepoint(mouse_pos) and event.type == MOUSEBUTTONDOWN:
                stages[count].remove(buttons)
                count = (count-1)%stage_num
                stages[count].add(buttons)
            if right_arrow.rect.collidepoint(mouse_pos) and event.type == MOUSEBUTTONDOWN:
                stages[count].remove(buttons)
                count = (count+1)%stage_num
                stages[count].add(buttons)
            if stages[count].rect.collidepoint(mouse_pos) and event.type == MOUSEBUTTONDOWN:
                menurun=False
                    #進入關卡
        redrawStartMenu()
    #bg1.clear()
    if count==0 or count==1:
        musicgame.mugame(copy.deepcopy(winter_tears),count+1)
    elif count==2:
        musicgame.mugame(copy.deepcopy(Mirror_night),count+1)

if __name__=='__main__':
    startmenu()


